import React from "react";
import Form from "../components/Form";
import Results from "../components/Results";
import API from "../utils/API";

const bookDatasresults = googleApiResults => {
    const bookDataArray = [];

    googleApiResults.map(bookData => {

      const formattedbookData = {
        title: bookData.volumeInfo.title,
        authors: bookData.volumeInfo.authors,
        description: bookData.volumeInfo.description,
        googleId: bookData.id,
        image: bookData.volumeInfo.imageLinks.thumbnail,
        link: bookData.volumeInfo.canonicalVolumeLink
      };

      bookDataArray.push(formattedbookData);
      return bookDataArray
    });
    return bookDataArray;
  };

  class Search extends React.Component {
    state = {
      search: '',
      results: [],
      error: ''
    };

    savebookData = event => {

      const chosenbookData = this.state.results.find(bookData => bookData.googleId === event.target.id);

      const newSave = {
        title: chosenbookData.title,
        authors: chosenbookData.authors,
        description: chosenbookData.description,
        googleId: chosenbookData.googleId,
        image: chosenbookData.image,
        link: chosenbookData.link
      };

      API.savebookData(newSave)
      console.log(newSave);
    };


    handleInputChange = event => {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
    };

    handleFormSubmit = event => {
      event.preventDefault();
      API.getbookDatas(this.state.search)
        .then(res => {
          const formattedArray = Results(res.data.items);
          this.setState({results: formattedArray});
        })
        .catch(err => console.log(err))
    };

    render() {
      return (
        <div className="container">

          <Form
            handleInputChange={this.handleInputChange}
            handleFormSubmit={this.handleFormSubmit}
          />
          <Results
            bookDatas={this.state.results}
            buttonAction={this.savebookData}
            buttonType="btn btn-success mt-2"
            buttonText="Save bookData"
          />
        </div>
      );
    }
  }

  export default Search;
